import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';

const Select = () => {
  const mountRef = useRef(null);

  useEffect(() => {
    const mountNode = mountRef.current;

    // シーン、カメラ、レンダラーの設定
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer();
    renderer.setSize(window.innerWidth, window.innerHeight);
    mountNode.appendChild(renderer.domElement);

    // ライトの追加
    const light = new THREE.AmbientLight(0xffffff); // 環境光
    scene.add(light);

    // GLTFLoaderを作成
    const loader = new GLTFLoader();

    // **1つ目のモデル（inudake move.glb）をロード**
    loader.load(
      '/models/inudake move.glb', // ファイルパス
      (gltf) => {
        const model = gltf.scene;
      
        const radius = 5; // モデルからカメラまでの距離
        
        const angle = 110 * (Math.PI / 180);
        const angle2 = 45 * (Math.PI / 180); // 45度をラジアンに変換

        camera.position.x = 0; // 必要に応じて調整
        camera.position.z = radius * Math.sin(angle); // 30度の高さ
        model.rotation.y = radius * Math.sin(angle2); // 適切な距離を維持
        camera.lookAt(0, 0, 0); // モデルの中心を向く

        scene.add(model); // シーンに追加

        // アニメーションの再生設定
        const mixer = new THREE.AnimationMixer(model);
        gltf.animations.forEach((clip) => {
          mixer.clipAction(clip).play();
        });

        // アニメーションを更新
        const clock = new THREE.Clock();
        const animate = () => {
          requestAnimationFrame(animate);
          const delta = clock.getDelta();
          mixer.update(delta);
          renderer.render(scene, camera);
        };
        animate();
      },
      undefined,
      (error) => {
        console.error('inudake move.glbの読み込みエラー:', error);
      }
    );

  

    return () => {
      mountNode.removeChild(renderer.domElement);
    };
  }, []);

  return <div ref={mountRef} style={{ width: '100vw', height: '100vh' }}></div>;
};

export default Select;
