import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../pages/styles/home.css';

const Home = () => {

  const navigate = useNavigate()

  const handleLogin = () => {
    navigate('/select')
  }

  const handleInstructions = () => {
    navigate('/Instructions')
  }
<link href="https://fonts.googleapis.com/earlyaccess/nicomoji.css" rel="stylesheet"></link>
  return (
    <div className="container">
      <h1 className='title'>VirtuPet</h1>
      <button className="btn1" onClick={handleLogin}>ログイン</button>
      <button className="btn2" onClick={handleInstructions}>使い方</button>
    </div>
  );
}

export default Home;
