import React, { useState } from 'react';
//import '../pages/styles/view.css';

const Veiw = () => {

  return (
    <div>
      <h1>ホームページ</h1>
      <label>
        表示する:
        <input 
          type="checkbox" 
          checked={isVisible} 
          onChange={handleVisibilityChange} 
        />
      </label>
      <div>
        <label>
          画像の大きさ:
          <input 
            type="number" 
            min="0.5" 
            max="3.0" 
            step="0.1" 
            value={animalSize} 
            onChange={handleSizeChange} 
          />
        </label>
        <button onClick={updateAnimalSettings}>サイズを更新</button>
      </div>
    </div>
  );
};

export default Veiw;
