import React, { useState } from 'react';
import '../animation/css/animal.css';

const Home = () => {
  const [isVisible, setIsVisible] = useState(true);

  // 動物の表示設定をサーバーに送信する関数
  const updateAnimalVisibility = async () => {
    const response = await fetch('http://localhost:5000/set_animal_settings', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ is_visible: isVisible })
    });

    const result = await response.json();
    console.log(result.status, result.settings);
  };

  // チェックボックスの変更を処理
  const handleVisibilityChange = (e) => {
    setIsVisible(e.target.checked);
    updateAnimalVisibility(); // 表示状態をサーバーに即時送信
  };

  return (
    <div>
      <h1>ホームページ</h1>
      <label>
        表示する:
        <input 
          type="checkbox" 
          checked={isVisible} 
          onChange={handleVisibilityChange} 
        />
      </label>
    </div>
  );
};

export default Home;
