import React, { useState } from 'react';
import '../animation/css/animal.css';

const Home = () => {
  const [isVisible, setIsVisible] = useState(true);
  const [animalSize, setAnimalSize] = useState(1.0);

  // 動物の設定をサーバーに送信する関数
  const updateAnimalSettings = async () => {
    const response = await fetch('http://localhost:5000/set_animal_settings', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ is_visible: isVisible, size: animalSize }) // サイズと表示を送信
    });

    const result = await response.json();
    console.log(result.status, result.settings);
  };

  // 表示設定の変更
  const handleVisibilityChange = (e) => {
    setIsVisible(e.target.checked);
    updateAnimalSettings();
  };

  // サイズ設定の変更
  const handleSizeChange = (e) => {
    setAnimalSize(e.target.value);
    updateAnimalSettings();  // サイズ変更を即時送信
  };

  return (
    <div>
      <h1>ホームページ</h1>
      <label>
        表示する:
        <input 
          type="checkbox" 
          checked={isVisible} 
          onChange={handleVisibilityChange} 
        />
      </label>
      <div>
        <label>
          画像の大きさ:
          <input 
            type="number" 
            min="0.5" 
            max="3.0" 
            step="0.1" 
            value={animalSize} 
            onChange={handleSizeChange} 
          />
        </label>
      </div>
    </div>
  );
};

export default Home;
