import React, { useState } from 'react';
//import '../pages/styles/view.css';

const Veiw = () => {
  const [content, setContent] = useState('ここに表示する内容を追加します');

  return (
    <div className="view-container">
      <h1>表示画面</h1>
      <p>{content}</p>
      <button onClick={() => setContent('新しい内容')}>内容を変更</button>
    </div>
  );
};

export default Veiw;
